var _=require('lodash');
var array=[1];
var sum=_.concat(array,2, [8],[[4]]);
console.log(sum);
console.log(array);
// difference
var result=_.difference([1,5,6],[1,7]);
console.log(result);
//differenceby
 var res=_.differenceBy([2.1,1.2],[2.3,3.4],Math.floor);
 console.log(res);
 var resu=_.differenceBy([{'x':1},{'x':2}],[{'x':1}],'x');
 console.log(resu);
//  join
var Newarr=['mahi','raju','rakesh','milky'];
var result1=_.join(Newarr,'+');
console.log(result1);
// without
var sum=require('lodash');
var myfruits=[2,5,8,9];
var result2=sum.without(myfruits,8);
console.log(result2);
// duplicate/uniq
var sum=require('lodash');
const mylist=['banana','applle','orange','applle','mango'];
const mynewarray=sum.uniq(mylist);
console.log(mynewarray);
// dropwhile
const _=require('lodash');
var numbers=[1,2,3,4,5,6,7,8];
var dropcondition=(x)=>x>3;
const result=_.dropWhile(numbers,dropcondition);
console.log(result);
